INSERT INTO `movie` VALUES ('320', '刘伟强', '6.9', '中国医生', '张涵予,袁泉,朱亚文,李晨,易烊千玺', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2666591984.jpg', '2021', '剧情,灾难', '中国大陆', '汉语普通话', '2021-07-09', '129', '216459', '16.1%,31.4%,36.2%,11.9%,4.4%', '电影根据新冠肺炎疫情防控斗争的真实事件改编，以武汉市金银潭医院为核心故事背景，同时兼顾武汉同济医院、武汉市肺科医院、武汉协和医院、武汉大学人民医院（湖北省人民医院）、火神山医院、方舱医院等兄弟单位，以武汉医护人员、全国各省市援鄂医疗队为人物原型，全景式记录波澜壮阔、艰苦卓绝的抗疫斗争。', '[{\"user\": \"\\u9b3c\\u8173\\u4e03\", \"start\": \"30\", \"time\": \"2021-07-09 08:14:32\", \"content\": \"\\u73b0\\u5728\\u7684\\u4e3b\\u65cb\\u5f8b\\u5df2\\u7ecf\\u672c\\u8d28\\u4e0a\\u4e0d\\u662f\\u7535\\u5f71\\u4e86\\uff0c\\u4e13\\u9898\\u7247\\u5f0f\\u60c5\\u666f\\u518d\\u73b0+\\u7535\\u5f71\\u5de5\\u4e1a\\u7ea7\\u6295\\u5165\\uff0c\\u8ba8\\u8bba\\u5b83\\u7684\\u5267\\u4f5c\\u8868\\u6f14\\u8c03\\u5ea6\\u90fd\\u662f\\u65e0\\u7528\\u7684\\uff0c\\u5e94\\u8be5\\u7528\\u62a5\\u544a\\u6587\\u5b66\\u7684\\u6807\\u51c6\\u6765\\u770b\\u8fd9\\u79cd\\u4f5c\\u54c1\\u3002\"}, {\"user\": \"\\u6625\\u5377\", \"start\": \"40\", \"time\": \"2021-07-09 10:08:20\", \"content\": \"\\u5c31\\u5f88\\u9b54\\u5e7b\\uff0c\\u75ab\\u60c5\\u8fd8\\u6ca1\\u5b8c\\u5168\\u7ed3\\u675f\\uff0c\\u4f46\\u662f\\u770b\\u5c4f\\u5e55\\u91cc\\u7684\\u4e00\\u5e55\\u5e55\\uff0c\\u597d\\u50cf\\u662f\\u4e0a\\u4e2a\\u4e16\\u7eaa\\u7684\\u4e8b\\u3002\"}, {\"user\": \"\\u4e0d\\u77e5\\u9053\\u53eb\\u4ec0\\u4e48\", \"start\": \"40\", \"time\": \"2021-07-09 09:24:32\", \"content\": \"\\u8fd9\\u79cd\\u9898\\u6750\\u5f88\\u96be\\u4e0d\\u8ba9\\u4eba\\u6d41\\u6cea\\uff0c\\u5f20\\u6db5\\u4e88\\uff0c\\u8881\\u6cc9\\u771f\\u7684\\u662f\\u5b9a\\u6d77\\u795e\\u9488\\uff0c\\u6731\\u4e9a\\u6587\\uff0c\\u5343\\u73ba\\u4e24\\u4e2a\\u4eba\\u7684\\u51fa\\u73b0\\u8ba9\\u6c89\\u91cd\\u7684\\u6c14\\u6c1b\\u6709\\u4e86\\u4e00\\u4e1d\\u8f7b\\u677e\\uff0c\\u75ab\\u60c5\\u5df2\\u7ecf\\u8fc7\\u53bb\\u5f88\\u4e45\\u4e86\\uff0c\\u6709\\u4e9b\\u753b\\u9762\\u597d\\u50cf\\u5728\\u88ab\\u6162\\u6162\\u5fd8\\u8bb0\\u2026\\u2026\\u611f\\u8c22\\u62