INSERT INTO `movie` VALUES ('447', '是枝裕和', '8.7', '小偷家族', '中川雅也,安藤樱,松冈茉优,城桧吏,佐佐木美结', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2530599636.jpg', '2018', '剧情,家庭,犯罪', '', '日本', '2018-08-03', '117', '735096', '45.1%,44.6%,9.4%,0.7%,0.2%', '东京的都市丛林中央，残存着一栋古旧寒酸的老房子，这里局促地生活着柴田一家五口人。在工地当临时工的男人阿治（Lily Franky 饰）经常带着儿子祥太（城桧吏 饰）到超市盗窃生活用品，这一天，他们回家路上遇到了独自待在户外的四岁女孩由里（佐佐木美结 饰）。妻子信代（安藤樱 饰）起初极力主张将女孩送回父母身边，但当看到女孩原生家庭的状况时又心生恻隐。原本柴田家就靠着老奶奶初枝（树木希林 饰）的养老金度日，而今多了一口人，自然更艰辛了几分。包括信代的妹妹亚纪（松冈茉优 饰）在内，虽然一家人游走在贫困和违法的边缘，但笃深的羁绊将他们紧紧联系在一起，使他们的心不会随着冰冷的都市而寒冷下去……\n                                    \n                                　　本片荣获第71届戛纳电影节金棕榈', '[{\"user\": \"\\u54af\\u54af\\u7cbe\", \"start\": \"40\", \"time\": \"2018-05-14 19:31:33\", \"content\": \"\\u5982\\u679c\\u8bf4\\u7231\\u4f60\\uff0c\\u8fd8\\u6253\\u4f60\\uff0c\\u90a3\\u4e00\\u5b9a\\u662f\\u8bf4\\u8c0e\\uff1b\\u5982\\u679c\\u7231\\u4f60\\uff0c\\u5c31\\u4f1a\\u50cf\\u5b89\\u85e4\\u6a31\\u4e00\\u6837\\u7d27\\u7d27\\u62b1\\u4f4f\\u4f60\\u3002\\uff08\\u6c89\\u6eba\\u4e8e\\u5b89\\u85e4\\u6a31\\u7684\\u7f8e\\u8272\\u65e0\\u6cd5\\u81ea\\u62d4\"}, {\"user\": \"\\u59e8\\u5988\\u7684\\u9e2d\", \"start\": \"40\", \"time\": \"2018-06-23 12:34:42\", \"content\": \"\\u6bd4\\u8d77\\u653e\\u4e0b\\u624b\\u673a\\uff0c\\u8fd9\\u672c\\u7535\\u5f71\\u5728\\u89c2\\u5f71\\u65f6\\u9700\\u8981\\u89c2\\u4f17\\u505a\\u7684\\u7b2c\\u4e00\\u6b65\\u662f\\uff1a\\u653e\\u4e0b\\u4e09\\u89c2\\u3002\"}, {\"user\": \"Lycidas\", \"start\": \"50\", \"time\": \"2018-08-03 21:59:46\", \"content\": \"\\u53ef\\u60dc\\u5168\\u90e8\\u7684\\u4e00\\u70b9\\u70b9\\u771f\\u5fc3\\uff0c\\u90fd\\u53ea\\u80fd\\u65e0\\u58f0\\u5730\\u8bf4\\u7ed9\\u81ea\\u5df1\\u542c\\u3002\"}, {\"user\": \"\\u5c0fA\", \"start\": \"30\", \"time\": \"2018-05-14 15:41:41\", \"content\": \"\\u6211\\u5f88\\u52aa\\u529b\\u8bd5\\u56fe\\u53bb\\u540c\\u60c5\\u4eba\\u7269\\uff0c\\u8fd8\\u662f\\u6ca1\\u6cd5\\u540c\\u60c5\\u4ed6\\u4eec\\u3002\\u60c5\\u8282\\u5f88cheap\\uff0c\\u76f4\\u767d\\u751f\\u