INSERT INTO `movie` VALUES ('492', '李安', '9.2', '饮食男女', '郎雄,杨贵媚,吴倩莲,王渝文,张艾嘉', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p1910899751.jpg', '1994', '剧情,家庭', '中国台湾,美国', '汉语普通话,闽南语,湖南话', '1994-08-03', '124', '535797', '63.2%,31.6%,4.8%,0.3%,0.1%', '台湾中国菜硕果仅存的大师老朱（郎雄）退休后，渐尝老年生活的诸多尴尬：每周日费心做出的一桌丰盛菜肴，并无将三个女儿（杨贵媚、吴倩莲、王渝文）齐齐拉到饭桌的吸引力，已经长大成人的她们，心里藏了许多比陪父亲吃饭更重要的事；多年老友的突然离世，令他在友情这块也有了缺口；而对厨师来讲最重要的味觉的丧失，则将其彻底推到人生的低谷。\n                                    \n                                　　三个女儿虽各忙各事，却也操心着老朱的晚年生活，计划着为其找个老伴，但她们没料到老朱早已悄无声息地谈起了“黄昏恋”。吃惊过后，老朱最看重的继承了他做菜天赋的二女儿家倩因为自身的生活经历，对父亲、她与父亲的关系有了新的认识，而对老朱来讲更大的惊喜，还在后面。', '[{\"user\": \"\\u8587\\u7f85\\u5c3c\\u5361\", \"start\": \"40\", \"time\": \"2006-04-12 13:55:54\", \"content\": \"\\u770b\\u5230\\u6700\\u540e\\uff0c\\u539f\\u6765\\u662f\\u8001\\u7238\\u6700\\u731b~~\"}, {\"user\": \"5\\u53ea\\u5c0f\\u718a\", \"start\": \"50\", \"time\": \"2012-09-14 01:31:55\", \"content\": \"\\u6211\\u4f1a\\u544a\\u8bc9\\u4f60\\u6211\\u53cd\\u590d\\u770b\\u4e86\\u4e94\\u904d\\u5f00\\u5934\\uff1f\\u53e3\\u6c34\\u7559\\u6210\\u6d77\"}, {\"user\": \"\\u5321\\u8efc\\u6b4c\", \"start\": \"50\", \"time\": \"2006-10-19 17:00:43\", \"content\": \"\\u674e\\u5b89\\u6700\\u597d\\u7684\\u4f5c\\u54c1\\u3002\\u592a\\u6d41\\u7545\\u4e86\\uff0c\\u8c03\\u9063\\u5f97\\u5b9c\\uff0c\\u5168\\u65e0\\u5e9f\\u53e5\\u3002\"}, {\"user\": \"\\u827e\\u5c0f\\u67ef\", \"start\": \"40\", \"time\": \"2006-06-20 03:34:34\", \"content\": \"\\u7ec6\\u817b\\u53c8\\u4e0d\\u505a\\u4f5c\\uff0c\\u674e\\u5b89\\u7684\\u4e00\\u8d2f\\u98ce\\u683c\\u3002\"}, {\"user\": \"\\u5df4\\u5586\", \"start\": \"50\", \"time\": \"2011-12-20 16:41:09\", \"content\": \"\\u674e\\u5b89\\u662f\\u7ec6\\u8282\\u5904\\u7406\\u7684\\u5927\\u5e08\\uff0c\\u5404\\u79cd\\u7684\\u94fa\\u57ab\\uff0c\\u5f71\\u5c04\\uff0c\\u548c\\u53cd\\u5dee\\uff0c\\u9020\\u6210\\u975e\\u5e38\\u51fa\\u5f69\\u7684\\u620f\\u5267\\u51b2\\u7a81\\u3002\\u63a8\\u8fdb\\u592a\\u6d41\\u7545\\u4e86\\uff0c\\u8fd0\\u955c\\u7a33\