INSERT INTO `movie` VALUES ('503', '奉俊昊', '8.9', '杀人回忆', '宋康昊,金相庆,金雷夏,宋在浩,边希峰', 'https://img3.doubanio.com/view/photo/s_ratio_poster/public/p1633113220.jpg', '2003', '剧情,动作,悬疑,惊悚,犯罪', '韩国', '韩语,英语', '2003-05-02', '132', '610139', '53.5%,38.0%,7.7%,0.6%,0.2%', '1986年，韩国京畿道华城郡，热得发昏的夏天，在田野边发现一具女尸，早已发臭。小镇警察朴探员（宋康昊饰）和汉城来的苏探员（金相庆饰）接手案件，唯一可证实的是这具女尸生前被强奸过。线索的严重缺乏让毫无经验的朴探员和搭档曹探员（金罗河饰）只凭粗暴逼供和第六感推断，几次将犯罪 嫌疑人屈打成招。而苏探员客观冷静，据理分析，几次排除嫌疑，警察内部为了证明与推翻矛盾不断，然而无辜女子还是接二连三被残忍杀害，他们只好达成共识一起合作。此时，一个极其符合作案特征的小青年（朴海日饰）成为最大嫌疑人，警方神经绷紧地锁定住他，同时DNA检测报告也被送往美国，然而案件并未在此处停止。', '[{\"user\": \"\\u6843\\u6843\\u6797\\u6797\", \"start\": \"50\", \"time\": \"2007-05-05 14:18:32\", \"content\": \"\\u8fd9\\u5e76\\u975e\\u4e00\\u4e2a\\u5bfb\\u6c42\\u771f\\u76f8\\u7684\\u6545\\u4e8b\\uff0c\\u800c\\u662f\\u5173\\u4e8e\\u771f\\u5230\\u5e95\\u76f8\\u5982\\u4f55\\u88ab\\u6574\\u6ca1\\u4e86\\u2026\\u2026\"}, {\"user\": \"\\u4e56\\u4e56\\u9686\\u5730\\u6d1e\", \"start\": \"40\", \"time\": \"2019-04-07 22:57:57\", \"content\": \"\\u504f\\u5411\\u4e8e\\u591a\\u4eba\\u6548\\u4eff\\u4f5c\\u6848\\uff0c\\u51f6\\u624b\\u4e0d\\u6b62\\u4e00\\u4e2a\\u3002\\u6700\\u540e\\u5973\\u5b66\\u751f\\u662f\\u5c0f\\u767d\\u8138\\u6740\\u7684\\u53ef\\u80fd\\u6027\\u5f88\\u5927\\uff01\\u8a00\\u884c\\u903c\\u4f9b\\u4e86\\u4e09\\u4e2a\\u4eba\\uff0c\\u53ea\\u6709\\u5c0f\\u767d\\u8138\\u6700\\u6001\\u5ea6\\u53cd\\u6297\\u3001\\u773c\\u795e\\u4e00\\u76f4\\u6709\\u7279\\u5199\\u662f\\u5145\\u6ee1\\u6068\\u610f\\u7684\\uff0c\\u56e0\\u4e3a\\u4ed6\\u5e76\\u6ca1\\u6709\\u6740\\u4eba\\u8fd8\\u8981\\u88ab\\u8bec\\u9677\\u3002\\u51fa\\u6765\\u540e\\u5728\\u9152\\u9986\\u559d\\u9152\\uff0c\\u5973\\u5b66\\u751f\\u4ece\\u7a97\\u524d\\u8d70\\u8fc7\\u4e5f\\u5bf9\\u4ed6\\u6307\\u70b9\\u8bae\\u8bba\\uff0c\\u8ba9\\u4ed6\\u89c9\\u5f97\\u81ea\\u5df1\\u7684\\u540d\\u58f0\\u5df2\\u7ecf\\u5f7b\\u5e95\\u6bc1\\u4e86\\u6d17\\u4e0d\\u6e05\\u4e86\\u3002\\u6240\\u4ee5\\u6700\\u540e\\u8b66\\u5bdf\\u5ab3\\u5987\\u548c\\u5973\\u5b66\\u751f\\u540c\\u65f6\\u51fa\\u73b0\\u4ed6