INSERT INTO `movie` VALUES ('552', '拉斯·霍尔斯道姆', '7.7', '一条狗的使命', '乔什·加德,布丽特·罗伯森,丹尼斯·奎德,佩吉·利普顿,K·J·阿帕', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2432493858.jpg', '2017', '剧情,喜剧,家庭', '美国', '英语', '2017-03-03', '101', '347893', '23.6%,42.9%,29.4%,3.5%,0.6%', '影片以汪星人的视角展现狗狗和人类的微妙情感，一只狗狗陪伴小主人长大成人，甚至为他追到了女朋友，后来它年迈死去又转世投胎变成其他性别和类型的汪，第二次轮回狗狗变成了警犬威风凛凛，再次转轮回，又成了陪伴一位单身女青年的小柯基犬。在经历了多次轮回之后，最终回到最初的主人身边。', '[{\"user\": \"\\u6dcb\", \"start\": \"30\", \"time\": \"2017-02-01 16:43:41\", \"content\": \"\\u56db\\u751f\\u56db\\u4e16\\uff0c\\u5341\\u91cc\\u9ea6\\u7530\"}, {\"user\": \"Carf\", \"start\": \"30\", \"time\": \"2017-02-18 16:03:13\", \"content\": \"\\u4e00\\u6761\\u5355\\u8eab\\u72d7\\u7684\\u4f7f\\u547d\\u5c31\\u662f\\u5e2e\\u52a9\\u5355\\u8eab\\u72d7\\u8131\\u5355\"}, {\"user\": \"\\u6c5f\\u5942\", \"start\": \"40\", \"time\": \"2017-03-03 17:06:55\", \"content\": \"\\u6240\\u6709\\u517b\\u8fc7\\u5ba0\\u7269\\u7684\\u4eba\\u90fd\\u4f1a\\u5e0c\\u671b\\u81ea\\u5df1\\u66fe\\u7ecf\\u7231\\u8fc7\\u7684\\u5ba0\\u7269\\u4f1a\\u56de\\u6765\\u627e\\u81ea\\u5df1\\u5427\\u3002\"}, {\"user\": \"\\u674e\\u5927\\u5a77\", \"start\": \"50\", \"time\": \"2017-01-30 12:56:40\", \"content\": \"\\u5988\\u4e2a\\u9e21\\u54ed\\u778e\\u4e86\"}, {\"user\": \"\\u904e\\u671f\\u8c93\\u7ce7\", \"start\": \"40\", \"time\": \"2017-03-10 22:43:47\", \"content\": \"\\u6211\\u53bb\\uff0c\\u4e5f\\u4e0d\\u8bf4\\u4e00\\u58f0\\u5f71\\u7247\\u5c31\\u7a81\\u7136\\u7ed3\\u675f\\u4e86\\uff01\\u706f\\u4eae\\u8d77\\u6765\\u4e00\\u5c4b\\u5b50\\u7684\\u4eba\\u6ee1\\u8138\\u773c\\u6cea\\u64e6\\u90fd\\u6765\\u4e0d\\u53ca\\uff01\\u4e0d\\u5c34\\u5c2c\\u5417\\uff1f\\u5dee\\u8bc4\\uff01\"}]', 'https://img2.doubanio.com/view/photo/sqxs/public/p2429007401.jpg,https://img3.doubanio.com/view/photo/sqxs/public/p2431824520.jpg,https://img2.doubanio.com/view/photo/sqxs/public/p2431824723.jpg,https://img2.doubanio.com/view/photo/sqxs/public/p2430117422.jpg', 'https://vt1.doubanio.com/202202171146/91def49dbf304a822