INSERT INTO `movies` VALUES ('70', '刘镇伟', '9', '大话西游之月光宝盒', '周星驰,吴孟达,罗家英,蓝洁瑛,莫文蔚', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2561721372.jpg', '1995', '喜剧,爱情,奇幻,古装', '中国香港,中国大陆', '粤语,汉语普通话', '2014-10-24', '87', '1093196', '59.8%,32.1%,7.4%,0.5%,0.2%', '孙悟空（周星驰）护送唐三藏（罗家英）去西天取经路上，与牛魔王合谋欲杀害唐三藏，并偷走了月光宝盒，此举使观音萌生将其铲除心思，经唐三藏请求，孙悟空被判五百年后重新投胎做人赎其罪孽。\n                                    \n                                　　五百年后孙悟空化身强盗头头至尊宝。当遇见预谋吃唐僧肉的妖怪姐妹蜘蛛精春三十娘（蓝洁瑛）和白骨精白晶晶（莫文蔚）时，因为五百年前孙悟空曾与白晶晶有过一段恋情，至尊宝与她一见钟情，但因菩提老祖将二人妖怪身份相告，至尊宝仍带领众强盗开始与二妖展开周旋，过程中，白晶晶为救至尊宝打伤春三十娘，自己也中毒受伤，为了救白晶晶，至尊宝去找春三十娘，遭白晶晶误会，绝望自杀，至尊宝开始用月光宝盒以期使时光倒流。', '[{\"user\": \"\\u7f57\\u5f18\\u9709\\u7d20\", \"start\": \"50\", \"time\": \"2010-07-26 18:35:55\", \"content\": \"\\u4e00\\u904d\\u70c2\\uff0c\\u4e24\\u904d\\u7b11\\uff0c\\u4e09\\u904d\\u54ed\\u3002\\u76d8\\u4e1d\\u5927\\u4ed9\\u7ed9\\u4e86\\u4e09\\u9897\\u75e3\\uff0c\\u65a7\\u5934\\u5e2e\\u4e3b\\u7ffb\\u8fc7\\u7167\\u5996\\u955c\\uff0c\\u770b\\u7740\\u955c\\u4e2d\\u7684\\u7334\\u5b50\\uff0c\\u6b64\\u5904\\u7684\\u914d\\u4e50\\u8db3\\u591f\\u9707\\u64bc\"}, {\"user\": \"\\u5b8b\\u963f\\u6155\", \"start\": \"50\", \"time\": \"2008-02-26 17:56:10\", \"content\": \"\\u5927\\u8bdd\\u897f\\u6e38\\u662f\\u4e00\\u4ee3\\u4eba\\u5fc3\\u4e2d\\u7684\\u7ecf\\u5178.\"}, {\"user\": \"Urth\\u00f3naD\'Mors\", \"start\": \"40\", \"time\": \"2011-09-29 12:40:02\", \"content\": \"\\u9022\\u5e74\\u8fc7\\u8282\\u5fc5\\u653e\\u5fc5\\u770b\\u7684\\u7ecf\\u5178 \\u6211\\u8bb0\\u5f97\\u7247\\u5b50\\u91cc\\u6700\\u540e\\u4e00\\u53e5\\u53f0\\u8bcd\\u662f\\uff1a\\u201c\\u4f60\\u770b\\u90a3\\u4e2a\\u4eba\\uff0c\\u597d\\u5947\\u602a\\u54df\\uff0c\\u8c61\\u4e00\\u6761\\u72d7\\u3002\\u201d \\u591a\\u5c11\\u7684\\u550f\\u5618\\u611f\\u6168\\u554a\"}, {\"user\": \"\\u8001\\u9e21\\uff5c\\u6276\\u7acb\", \"start\": \"50\", \"time\": \"2008-01-10 17:00:24\", \"content\": \"\\u7247\\u5c3e\\u66f2\\u771f\\u7684\\u5f88\\u597d\\u542c\"}, {\"user\": \"\\u5357\\u7b19\", \"start\": \"40