INSERT INTO `movies` VALUES ('176', '程小东', '8.8', '倩女幽魂', '张国荣,王祖贤,午马,刘兆铭,林威', 'https://img9.doubanio.com/view/photo/s_ratio_poster/public/p2414157745.jpg', '1987', '爱情,奇幻,武侠,古装', '中国香港', '粤语', '2011-04-30', '98', '653061', '48.2%,41.8%,9.4%,0.4%,0.1%', '书生宁采臣（张国荣饰）收账不成，无处可归，遂夜宿鬼寺兰若寺，遇上侠士燕赤霞（午马饰），二人成为邻居。一日偶遇艳女小倩（王祖贤饰），二人两情相悦，但小倩乃是被树妖姥姥控制的鬼魂，专门迷惑精壮男子代姥姥吸取阳气。小倩见宁采臣心地善良不忍加害，并与他感情逐渐加深。宁采臣欲救小倩脱离姥姥魔掌，但苦于姥姥功力深厚，小倩无法脱离其魔掌。二人遂向燕赤霞求救......', '[{\"user\": \"\\u8d99\\u5c0f\\u6f01\", \"start\": \"50\", \"time\": \"2008-05-04 17:56:36\", \"content\": \"\\u9019\\u500b\\u8076\\u5c0f\\u5029\\uff0c\\u723e\\u7b49\\u4f11\\u60f3\\u518d\\u8d85\\u8d8a\\uff01\"}, {\"user\": \"missfaye\", \"start\": \"50\", \"time\": \"2005-12-13 23:05:06\", \"content\": \"\\u5c0f\\u8d24\\u4e4b\\u7709\\u5b87\\uff0c\\u6ca1\\u6709\\u518d\\u6f02\\u4eae\\u7684\"}, {\"user\": \"with\", \"start\": \"50\", \"time\": \"2010-05-28 18:34:44\", \"content\": \"\\u672a\\u89c1\\u8fc7\\u7535\\u5f71\\u9662\\u5728\\u975e\\u8282\\u5047\\u65e5\\u53ea\\u5409\\u7740\\u7b2c\\u4e00\\u6392\\uff0c\\u672a\\u542c\\u8fc7\\u7535\\u5f71\\u6563\\u573a\\u65f6\\u6709\\u5982\\u6b64\\u70ed\\u70c8\\u7684\\u638c\\u58f0\\uff0c\\u672a\\u89c1\\u8fc7\\u5168\\u573a\\u89c2\\u4f17\\u90fd\\u7b49\\u5230\\u94f6\\u5e55\\u5f7b\\u5e95\\u9ed1\\u4e86\\u624d\\u8d77\\u8eab\\u79bb\\u573a\\uff0c\\u672a\\u89c1\\u8fc7\\u6253\\u5b57\\u5e55\\u65f6\\u6709\\u4eba\\u62cd\\u7167\\u7559\\u5ff5\\uff0c\\u5341\\u4e03\\u5e74\\u540e\\uff0c\\u300a\\u5029\\u5973\\u5e7d\\u9b42\\u300b\\u4ecd\\u7136\\u7528\\u4e8b\\u5b9e\\u544a\\u8bc9\\u4ed6\\u4eba\\u201c\\u7ecf\\u5178\\u201d\\u4e8c\\u5b57\\u5f53\\u4e4b\\u65e0\\u6127\"}, {\"user\": \"REDEYESKi\", \"start\": \"40\", \"time\": \"2008-07-21 14:39:12\", \"content\": \"\\u597d\\u7f8e\\u7684\\u738b\\u7956\\u8ce2\\uff5e\"}, {\"user\": \"\\u5f71\\u5fd7\", \"start\": \"40\", \"time\": \"2011-05-08 00:56:48\", \"content\": \"2011\\u65b0\\u7248\\u548c1987\\u65e7\\u7248\\u8fde\\u7740\\u5728\\u5f71\\u9662\\u770b\\u7684\\uff0c\\u524d\\u540e\\u5d