INSERT INTO `movies` VALUES ('244', '柯汶利', '7.5', '误杀', '肖央,谭卓,陈冲,姜皓文,秦沛', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2614628862.jpg', '2019', '剧情,悬疑,犯罪', '中国大陆', '汉语普通话,泰语', '2019-12-13', '112', '800371', '15.9%,49.9%,29.2%,4.0%,0.9%', '李维杰（肖央 饰）与妻子阿玉（谭卓 饰）来泰打拼17年，膝下育有两个女儿，年届四十的他靠开设网络公司为生，为人也颇得小镇居民的好感，而这一切美好却被突如其来的不速之客打破。这个充斥走私，贩毒活动的边陲小镇，各种权力交织碾压公平正义。李维杰的大女儿平平（许文珊 饰）被督察长拉韫（陈冲 饰）的儿子素察（边天扬 饰）迷奸，因反抗误杀对方。李维杰曾亲眼目睹督察长滥用私刑，深知法律无用，为了维护女儿，捍卫家人，李维杰埋尸掩盖一切证据，在时间与空间的交错缝隙中，与警方在身心层面，展开了殊死一搏的较量。', '[{\"user\": \"\\u5c0f\\u660e\\u660e\", \"start\": \"50\", \"time\": \"2019-12-07 21:35:57\", \"content\": \"\\u6211\\u60f3\\u8bf4\\u8fd9\\u5e94\\u8be5\\u662f\\u6211\\u770b\\u5230\\u76842019\\u5e74\\u6700\\u597d\\u7684\\u56fd\\u4ea7\\u5546\\u4e1a\\u7247\\u4e86\\u3002\\u8fd9\\u662f\\u4e0d\\u662f\\u4f50\\u8bc1\\u4e86\\u8096\\u592e\\u662f\\u5510\\u4eba\\u8857\\u63a2\\u6848\\u91cc\\u6392\\u884c\\u699c\\u7b2c\\u4e00\\u7684Q\\uff1f\"}, {\"user\": \"Janie\", \"start\": \"30\", \"time\": \"2019-12-09 22:14:50\", \"content\": \"\\u500b\\u4eba\\u89ba\\u5f97\\u5230\\u61fa\\u6094\\u5854\\u90a3\\u88e1\\u7d50\\u675f\\u5c31\\u5dee\\u4e0d\\u591a\\u4e86\\uff0c\\u5f8c\\u9762\\u5f37\\u884c\\u5347\\u83ef\\u7684\\u7d50\\u5c40\\u6bc0\\u4e86\\u6240\\u6709\"}, {\"user\": \"\\u561f\\u561f\\u718a\\u4e4b\\u7236\", \"start\": \"40\", \"time\": \"2019-12-07 16:23:42\", \"content\": \"\\u597d\\u4e8e\\u5370\\u5ea6\\u539f\\u7248\\uff0c\\u5c06\\u4e0d\\u75db\\u4e0d\\u75d2\\u7684\\u73b0\\u5b9e\\u4e3b\\u4e49\\u88f9\\u811a\\u5e03\\u6539\\u9020\\u6210\\u9ad8\\u5ea6\\u6d53\\u7f29\\u7684\\u793e\\u4f1a\\u5bd3\\u8a00\\u3002\\u7f8a\\u547d\\u3001\\u8499\\u592a\\u5947\\u3001\\u67b6\\u7a7a\\u56fd\\u5ea6\\u3001\\u591a\\u91cd\\u4eba\\u7269\\u8eab\\u4efd\\uff0c\\u53ef\\u4ee5\\u7f6e\\u653e\\u5728\\u4efb\\u4f55\\u653f\\u6cbb\\u8bed\\u5883\\u4e0b\\u8fdb\\u884c\\u89e3\\u8bfb\\u3002\\u5bf9\\u51c6\\u9762\\u5b54\\u7684\\u957f\\u4e45\\u51dd\\u89c6\\uff0c\\u662f\\u8bd5\\u63a2\\u548c\\u6311\\u8845\\uff0c\\u4e5f\\u662f\\u63a7\\u