INSERT INTO `movies` VALUES ('254', '吴宇森', '8.6', '英雄本色', '周润发,狄龙,张国荣,朱宝意,李子雄', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2504997087.jpg', '1986', '剧情,动作,犯罪', '中国香港', '粤语,汉语普通话,英语', '2017-11-17', '95', '467730', '44.8%,43.3%,11.2%,0.5%,0.1%', '香港某个国际伪钞集团重要分子宋子豪（狄龙 饰）和Mark（周润发 饰）情同手足，某次宋子豪带手下谭成（李子雄 饰）去台北交易时被其出卖受枪伤被捕入狱，为替好兄弟报仇，Mark孤身赴台，结果被人打成瘸子，江湖地位自此一落千丈，而宋子豪的父亲也在不久丧命。 一向视宋子豪为偶像的宋子杰（张国荣 饰）得知哥哥的真实身份后，对黑社会及他恨之入骨，立誓利用警察身份将黑帮势力斩草除根，宋子豪出狱后不顾苦等他想重创辉煌的Mark的劝阻，决心弃暗投明，无奈得不到宋子杰的谅解。而此时已成帮会老大的谭成欲找宋子豪再合作，遭拒绝后放言会害宋子杰，被逼走投无路，宋子豪决定再与Mark连手共抗强敌。', '[{\"user\": \"\\u53cd\\u5f15\\u529b\\u75af\\u5b50\", \"start\": \"50\", \"time\": \"2010-04-05 22:09:02\", \"content\": \"\\u6709\\u51e0\\u4e2a\\u955c\\u5934\\u91cc\\uff0c\\u9f99\\u54e5\\u4f1a\\u53d1\\u5149\"}, {\"user\": \"\\u8d99\\u5c0f\\u6f01\", \"start\": \"50\", \"time\": \"2008-05-08 22:22:29\", \"content\": \"\\u6211\\u89aa\\u611b\\u7684\\u5c0f\\u99ac\\u54e5\\uff0c\\u6642\\u81f3\\u4eca\\u65e5\\u4f60\\u8b93\\u6211\\u611f\\u5606\\u82f1\\u96c4\\u9072\\u66ae\\u66f4\\u662f\\u7a2e\\u6298\\u78e8\\u2026\\u2026\"}, {\"user\": \"Hey Johnny\", \"start\": \"40\", \"time\": \"2012-04-17 15:08:04\", \"content\": \"\\u3010\\u65b0\\u827a\\u57ce\\u3011\\u4e09\\u4e2a\\u7537\\u4e3b\\u6f14\\u5f97\\u90fd\\u4e0d\\u9519\\uff0c\\u4f46\\u8fd8\\u662f\\u5468\\u6da6\\u53d1\\u7684\\u5c0f\\u9a6c\\u54e5\\u6700\\u7cbe\\u5f69\\uff0c\\u51e0\\u6b21\\u76f4\\u6233\\u6cea\\u70b9\\u90fd\\u662f\\u4ed6\\u548c\\u72c4\\u9f99\\u7684\\u5bf9\\u624b\\u620f\\u3002\\u524d\\u9762\\u620f\\u4efd\\u91cc\\u54e5\\u54e5\\u707f\\u70c2\\u7684\\u7b11\\u5bb9\\u8ba9\\u4eba\\u6253\\u5fc3\\u5e95\\u6b22\\u559c\\uff0c\\u5982\\u4eca\\u91cd\\u6e29\\u611f\\u52a8\\u6ee1\\u6ee1\\u3002\\u5f90\\u514b\\u505a\\u76d1\\u5236\\uff0c\\u8fd8\\u8e29\\u4e86\\u4e00\\u811a\\u5ba2\\u4e32\\u3002\"}, {\"user\": \"H\", \"start\": \"50\", \"time\": \"2016-12-31 10:01:49\", \"content\": \"\\u5c0f\\u65f6\\u5019\\u8fd8\\u770b\\u4e0d\\u61c2\\u8fd9\\u7247\\u7684\\u60c5\\u6000\\u548c\\u610f\\u4e49 \\u53ea\